member.example:
McObjectId id = GetId();

// Получение объекта из документа.
McObject obj = id.GetObject();
McEntity entity = id.GetObject() as McEntity;
DbGeometry geometry = id.GetObject() as DbGeometry;

// Получение Db-объекта, связанного с возвращаемым
if (obj != null) {
	McDbEntity dbEntity = obj.Cast < McDbEntity >();
}
if (entity != null) {
	McDbEntity dbEntity = entity.DbEntity;
}
if (geometry != null) {
	McDbEntity dbEntity = geometry.DbEntity;
}
